pragma solidity 0.8.9;

import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

import "./IWhitelistVaultFactory.sol";
import "./Beacon.sol";

contract VaultFactory is OwnableUpgradeable {
    using EnumerableSet for EnumerableSet.AddressSet;
    event VaultCreated(address owner, address newVaultAddress);

    EnumerableSet.AddressSet private deployedVaults;

    address public admin;
    address public serviceFeeAddress;

    VaultBeacon public beacon;

    uint256 public tax;
    uint256 public serviceFee;
    uint256 public emergencyTax;

    constructor(address _defaultAdmin) {
        admin = _defaultAdmin;
    }

    function initialize(address _initBlueprint, address _serviceFeeAddress) public initializer {
        __Ownable_init();
        beacon = new VaultBeacon(_initBlueprint);
        serviceFeeAddress = _serviceFeeAddress;
        tax = 1 ether;
        serviceFee = 10;
    }

    function getAll(uint32 startingIndex, uint256 length) public view returns (address[] memory) {
        uint256 end = length > deployedVaults.length() ? deployedVaults.length() : length;

        address[] memory ret = new address[](end - startingIndex);
        for (uint32 i = startingIndex; i < end; i++) {
            ret[i] = deployedVaults.at(i);
        }
        return ret;
    }

    function getVaultAt(uint32 _index) public view returns (address) {
        return deployedVaults.at(_index);
    }

    function hasVault(address _addr) public view returns (bool) {
        return deployedVaults.contains(_addr);
    }

    function getBeacon() public view returns (address) {
        return address(beacon);
    }

    function getImplementation() public view returns (address) {
        return beacon.implementation();
    }

    function setServiceFeeAddress(address _addr) public onlyOwner {
        serviceFeeAddress = _addr;
    }

    function setTax(uint256 _tax) public onlyOwner {
        tax = _tax;
    }

    function setServiceTax(uint256 _tax) public onlyOwner {
        serviceFee = _tax;
    }

    function addVault(address _idoAddress) internal {
        deployedVaults.add(_idoAddress);
    }

    function totalVaults() public view returns (uint256) {
        return deployedVaults.length();
    }

    function version() public pure returns (uint256) {
        return 1;
    }

    uint256[43] private __gap;
}
