// Solidity files have to start with this pragma.
// It will be used by the Solidity compiler to validate its version.

pragma solidity 0.8.9;

import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";

import "../../presets/SimpleTimelockStakingVault.sol";
import "../../lib/StakingUtils.sol";
import "../Factory.sol";
import "../../utils/RoleAccess.sol";

contract SimpleTimeLockStakingVaultFactory is VaultFactory {
    constructor(address _defaultAdmin) VaultFactory(_defaultAdmin) {}

    function create(
        StakingUtils.StakingConfiguration memory config,
        StakingUtils.TaxConfiguration memory taxConfig,
        StakingUtils.LockConfiguration memory lockConfig
    ) public payable {
        require(msg.value >= tax, "tax");
        payable(serviceFeeAddress).transfer(address(this).balance);

        taxConfig.hpayFee = 0;
        taxConfig.hpayFeeAddress = serviceFeeAddress;

        BeaconProxy stakingProxy = new BeaconProxy(
            address(beacon),
            abi.encodeWithSelector(
                SimpleTimeLockStakingVault(payable(address(0))).initialize.selector,
                config,
                taxConfig,
                lockConfig
            )
        );

        addVault(address(stakingProxy));
        RoleAccess newVault = RoleAccess(payable(stakingProxy));

        AccessControlUpgradeable(newVault).grantRole(newVault.OWNER_ROLE(), msg.sender);
        AccessControlUpgradeable(newVault).grantRole(newVault.MANAGER_ROLE(), msg.sender);
        AccessControlUpgradeable(newVault).grantRole(newVault.OWNER_ROLE(), admin);
        AccessControlUpgradeable(newVault).grantRole(newVault.DEFAULT_ADMIN_ROLE(), admin);
        AccessControlUpgradeable(newVault).grantRole(newVault.MANAGER_ROLE(), owner());
        emit VaultCreated(msg.sender, address(stakingProxy));
    }
}
